mapper.select:
select f.pler_name ,POSITION_NUM,pler_price ,mem_Id
from player p , formation f
where p.pler_name(+) = f.pler_name and mem_id = #{memId}

select  pler_name plerName
from formation
where mem_id = #{memId} and pler_name = #{plerName}

select mem_id memId, pler_name plerName ,POSITION_NUM positionNum ,pler_image plerImage
from formation
where mem_id = #{memId}
